<span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">getwindows</span>(<span style="color:#8b008b;font-weight:bold">const</span> TCHAR* classname,
    <span style="color:#8b008b;font-weight:bold">const</span> gprocess::WindowInfo&amp; info,
    std::vector&lt;std::shared_ptr&lt;gprocess::WindowInfo&gt;&gt;&amp; result)
{
    std::vector&lt;std::shared_ptr&lt;gprocess::WindowInfo&gt;&gt; tmp;
    TCHAR classname_[MAX_PATH] = { <span style="color:#b452cd">0</span> };
    <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = <span style="color:#b452cd">0</span>; i &lt; info.childs.size(); ++i)
    {
        GetClassName(<span style="color:#8b008b;font-weight:bold">reinterpret_cast</span>&lt;HWND&gt;(info.childs[i]-&gt;window), classname_, _countof(classname_));
        <span style="color:#8b008b;font-weight:bold">if</span> (_tcscmp(classname_, classname) == <span style="color:#b452cd">0</span>)
        {
            tmp.push_back(info.childs[i]);
        }
    }
    <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#8b008b;font-weight:bold">const</span> <span style="color:#8b008b;font-weight:bold">auto</span>&amp; each : tmp)
    {
        result.push_back(each);
    }
    <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">0</span>;
}
<span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">getwindows</span>(<span style="color:#8b008b;font-weight:bold">const</span> TCHAR* classname,
    <span style="color:#8b008b;font-weight:bold">const</span> std::vector&lt;std::shared_ptr&lt;gprocess::WindowInfo&gt;&gt;&amp; windows,
