}

<span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">main</span>(<span style="color:#00688b;font-weight:bold">int</span> argc, <span style="color:#00688b;font-weight:bold">char</span>** argv)
{
    libvlc_instance_t* inst_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    libvlc_media_t* media_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    libvlc_media_player_t* player_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    libvlc_media_list_t* list_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    libvlc_media_list_player_t* plist_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    HWND wnd_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;

    std::vector&lt;gprocess::ProcessInfo&gt; result;
    gprocess::WindowInfo windowinfo;
    std::vector&lt;std::shared_ptr&lt;gprocess::WindowInfo&gt;&gt; windows;
    std::vector&lt;std::shared_ptr&lt;gprocess::WindowInfo&gt;&gt; tmpwindows;
    <span style="color:#8b008b;font-weight:bold">auto</span> ret = gprocess::gethandle(<span style="color:#cd5555">&#34;Taskmgr.exe&#34;</span>, result);
    CHECKNEQUALRET(ret, <span style="color:#b452cd">0</span>);
    windowinfo.processid = result[<span style="color:#b452cd">0</span>].processid;
    ret = gprocess::getallwindows(&amp;windowinfo);
    CHECKNEQUALRET(ret, <span style="color:#b452cd">0</span>);

    ret = getwindows(TEXT(<span style="color:#cd5555">&#34;TaskManagerWindow&#34;</span>), windowinfo, windows);
    CHECKNEQUALRET(ret, <span style="color:#b452cd">0</span>);
    ret = getwindows(TEXT(<span style="color:#cd5555">&#34;NativeHWNDHost&#34;</span>), windows, tmpwindows);
    CHECKNEQUALRET(ret, <span style="color:#b452cd">0</span>);
    windows.clear();
    windows.swap(tmpwindows);
    ret = getwindows(TEXT(<span style="color:#cd5555">&#34;DirectUIHWND&#34;</span>), windows, tmpwindows);
