</span><span style="color:#1e889b">#include</span> <span style="color:#1e889b">&lt;thread&gt;</span><span style="color:#1e889b">
</span><span style="color:#1e889b">#include</span> <span style="color:#1e889b">&lt;atomic&gt;</span><span style="color:#1e889b">
</span><span style="color:#1e889b"></span>
<span style="color:#1e889b">#ifdef _WIN32
</span><span style="color:#1e889b">#include</span> <span style="color:#1e889b">&lt;Windows.h&gt;</span><span style="color:#1e889b">
</span><span style="color:#1e889b">#define ssize_t SSIZE_T
</span><span style="color:#1e889b">#endif
</span><span style="color:#1e889b">#include</span> <span style="color:#1e889b">&lt;vlc/vlc.h&gt;</span><span style="color:#1e889b">
</span><span style="color:#1e889b"></span>
<span style="color:#1e889b">#define CHECKEQUALRET(ret, compare)\
</span><span style="color:#1e889b">if(ret == compare)\
</span><span style="color:#1e889b">{\
</span><span style="color:#1e889b">    std::cerr &lt;&lt; &#34;error ocurred in &#34; &lt;&lt; __FILE__\
</span><span style="color:#1e889b">              &lt;&lt; &#34;`s line &#34; &lt;&lt; __LINE__\
</span><span style="color:#1e889b">              &lt;&lt; &#34;, error &#34; &lt;&lt; ret;\
</span><span style="color:#1e889b">    goto END;\
</span><span style="color:#1e889b">}
</span><span style="color:#1e889b"></span>
<span style="color:#1e889b">#define CHECKNEQUALRET(ret, compare)\
</span><span style="color:#1e889b">if(ret != compare)\
</span><span style="color:#1e889b">{\
</span><span style="color:#1e889b">    std::cerr &lt;&lt; &#34;error ocurred in &#34; &lt;&lt; __FILE__\
</span><span style="color:#1e889b">              &lt;&lt; &#34;`s line &#34; &lt;&lt; __LINE__\
</span><span style="color:#1e889b">              &lt;&lt; &#34;, error &#34; &lt;&lt; ret;\
</span><span style="color:#1e889b">    goto END;\
</span><span style="color:#1e889b">}
</span><span style="color:#1e889b"></span>
<span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">int</span> WIDTH = <span style="color:#b452cd">200</span>;
<span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">int</span> HEIGHT = <span style="color:#b452cd">50</span>;
<span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">char</span>* out_buffer = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
<span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">char</span>* tmp_buffer = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
<span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">char</span>* print_buffer = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
std::atomic&lt;<span style="color:#00688b;font-weight:bold">bool</span>&gt; atomiclock;
std::atomic&lt;<span style="color:#00688b;font-weight:bold">bool</span>&gt; gotframe = <span style="color:#658b00">false</span>;
<span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">void</span>* <span style="color:#008b45">lock</span>(<span style="color:#00688b;font-weight:bold">void</span>* data, <span style="color:#00688b;font-weight:bold">void</span>** p_pixels)
{
    <span style="color:#8b008b;font-weight:bold">while</span> (atomiclock)
    {
        std::this_thread::sleep_for(std::chrono::microseconds(<span style="color:#b452cd">10</span>));
    }
    atomiclock = <span style="color:#658b00">true</span>;
    *p_pixels = out_buffer;
    <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#b452cd">0</span>;
}
<span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">unlock</span>(<span style="color:#00688b;font-weight:bold">void</span>* data, <span style="color:#00688b;font-weight:bold">void</span>* id, <span style="color:#00688b;font-weight:bold">void</span>* <span style="color:#8b008b;font-weight:bold">const</span>* p_pixels)
{
    atomiclock = <span style="color:#658b00">false</span>;
    gotframe = <span style="color:#658b00">true</span>;
}
<span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">display</span>(<span style="color:#228b22">/*void* data, void* id*/</span>)
{
    atomiclock = <span style="color:#658b00">true</span>;
    <span style="color:#8b008b;font-weight:bold">if</span> (!gotframe)
    {
        atomiclock = <span style="color:#658b00">false</span>;
        std::this_thread::sleep_for(std::chrono::microseconds(<span style="color:#b452cd">10</span>));
        <span style="color:#8b008b;font-weight:bold">return</span>;
    }
    memcpy(tmp_buffer, out_buffer, HEIGHT * WIDTH * <span style="color:#b452cd">4</span>);
    gotframe = <span style="color:#658b00">false</span>;
    atomiclock = <span style="color:#658b00">false</span>;

    HANDLE hConsoleOutput = GetStdHandle(STD_OUTPUT_HANDLE);
    COORD pos = { <span style="color:#b452cd">0</span> };
    SetConsoleCursorPosition(hConsoleOutput, pos);

    RGBQUAD* rgba = <span style="color:#8b008b;font-weight:bold">reinterpret_cast</span>&lt;RGBQUAD*&gt;(tmp_buffer);
    <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = <span style="color:#b452cd">0</span>; i &lt; HEIGHT; ++i)
    {
        <span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> j = <span style="color:#b452cd">0</span>; j &lt; WIDTH; ++j)
        {
            <span style="color:#8b008b;font-weight:bold">auto</span> point = rgba[WIDTH * i + j];
            <span style="color:#8b008b;font-weight:bold">auto</span> light = (point.rgbRed + point.rgbGreen + point.rgbBlue) / <span style="color:#b452cd">3</span>;
            print_buffer[i * (WIDTH + <span style="color:#b452cd">2</span>) + j] = light &gt; <span style="color:#b452cd">127</span> ? <span style="color:#cd5555">&#39;*&#39;</span> : <span style="color:#cd5555">&#39; &#39;</span>;
        }
        print_buffer[i * (WIDTH + <span style="color:#b452cd">2</span>) + WIDTH] = <span style="color:#cd5555">&#39;\r&#39;</span>;
        print_buffer[i * (WIDTH + <span style="color:#b452cd">2</span>) + WIDTH + <span style="color:#b452cd">1</span>] = <span style="color:#cd5555">&#39;\n&#39;</span>;
    }
    puts(print_buffer);
}

<span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">main</span>(<span style="color:#00688b;font-weight:bold">int</span> argc, <span style="color:#00688b;font-weight:bold">char</span>** argv)
{
    libvlc_instance_t* inst_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    libvlc_media_t* media_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    libvlc_media_player_t* player_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    libvlc_media_list_t* list_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    libvlc_media_list_player_t* plist_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    <span style="color:#00688b;font-weight:bold">int</span> ret = <span style="color:#b452cd">0</span>;

    std::cout &lt;&lt; <span style="color:#cd5555">&#34;Usage: Character-player [media file] [out width] [out height]&#34;</span> &lt;&lt; std::endl;

    <span style="color:#8b008b;font-weight:bold">if</span> (argc &gt;= <span style="color:#b452cd">4</span>)
    {
        WIDTH = atoi(argv[<span style="color:#b452cd">2</span>]);
        HEIGHT = atoi(argv[<span style="color:#b452cd">3</span>]);
    }

    HANDLE hConsoleOutput = GetStdHandle(STD_OUTPUT_HANDLE);
    COORD size = { WIDTH + <span style="color:#b452cd">4</span>, HEIGHT + <span style="color:#b452cd">5</span> };
    SetConsoleScreenBufferSize(hConsoleOutput, size);
    SMALL_RECT rc = { <span style="color:#b452cd">0</span>,<span style="color:#b452cd">0</span>, WIDTH + <span style="color:#b452cd">1</span>, HEIGHT + <span style="color:#b452cd">1</span> };
    SetConsoleWindowInfo(hConsoleOutput, <span style="color:#658b00">true</span>, &amp;rc);

    libvlc_log_close(<span style="color:#8b008b;font-weight:bold">nullptr</span>);

    <span style="color:#00688b;font-weight:bold">bool</span> exit = <span style="color:#658b00">false</span>;
    std::<span style="color:#8b008b;font-weight:bold">thread</span> th([&amp;]()
    {
        <span style="color:#8b008b;font-weight:bold">while</span> (!exit)
        {
            <span style="color:#8b008b;font-weight:bold">while</span> (atomiclock)
            {
                std::this_thread::sleep_for(std::chrono::microseconds(<span style="color:#b452cd">10</span>));
            }
            display();
        }
    });

    inst_ = libvlc_new(<span style="color:#b452cd">0</span>, <span style="color:#8b008b;font-weight:bold">nullptr</span>);
    CHECKEQUALRET(inst_, <span style="color:#8b008b;font-weight:bold">nullptr</span>);
    media_ = libvlc_media_new_path(inst_, argc &lt;= <span style="color:#b452cd">1</span> ? <span style="color:#cd5555">&#34;badapple.mp4&#34;</span> : argv[<span style="color:#b452cd">1</span>]);
    CHECKEQUALRET(media_, <span style="color:#8b008b;font-weight:bold">nullptr</span>);

    out_buffer = <span style="color:#8b008b;font-weight:bold">static_cast</span>&lt;<span style="color:#00688b;font-weight:bold">char</span>*&gt;(malloc(HEIGHT * WIDTH * <span style="color:#b452cd">4</span>));
    CHECKEQUALRET(out_buffer, <span style="color:#8b008b;font-weight:bold">nullptr</span>);
    tmp_buffer = <span style="color:#8b008b;font-weight:bold">static_cast</span>&lt;<span style="color:#00688b;font-weight:bold">char</span>*&gt;(malloc(HEIGHT * WIDTH * <span style="color:#b452cd">4</span>));
    CHECKEQUALRET(tmp_buffer, <span style="color:#8b008b;font-weight:bold">nullptr</span>);
    print_buffer = <span style="color:#8b008b;font-weight:bold">static_cast</span>&lt;<span style="color:#00688b;font-weight:bold">char</span>*&gt;(malloc(HEIGHT * (WIDTH + <span style="color:#b452cd">2</span>) + <span style="color:#b452cd">1</span>));
    CHECKEQUALRET(print_buffer, <span style="color:#8b008b;font-weight:bold">nullptr</span>);
    print_buffer[HEIGHT * (WIDTH + <span style="color:#b452cd">2</span>)] = <span style="color:#cd5555">&#39;\0&#39;</span>;

    <span style="color:#228b22">//player_ = libvlc_media_player_new_from_media(media_);
</span><span style="color:#228b22"></span>    player_ = libvlc_media_player_new(inst_);
    CHECKEQUALRET(player_, <span style="color:#8b008b;font-weight:bold">nullptr</span>);

    libvlc_video_set_callbacks(player_, lock, unlock, <span style="color:#8b008b;font-weight:bold">nullptr</span>, <span style="color:#b452cd">0</span>);
    libvlc_video_set_format(player_, <span style="color:#cd5555">&#34;RGBA&#34;</span>, WIDTH, HEIGHT, WIDTH * <span style="color:#b452cd">4</span>);
    <span style="color:#228b22">//libvlc_media_player_set_hwnd(player_, GetDesktopWindow());
</span><span style="color:#228b22"></span>    
    <span style="color:#228b22">// play loop
</span><span style="color:#228b22"></span>    list_ = libvlc_media_list_new(inst_);
    plist_ = libvlc_media_list_player_new(inst_);
    libvlc_media_list_add_media(list_, media_);
    libvlc_media_list_player_set_media_list(plist_, list_);
    libvlc_media_list_player_set_media_player(plist_, player_);
    libvlc_media_list_player_set_playback_mode(plist_, libvlc_playback_mode_loop);
    libvlc_media_list_player_play(plist_);

    std::cin.get();

END:
    exit = <span style="color:#658b00">true</span>;
    <span style="color:#8b008b;font-weight:bold">if</span> (th.joinable())
    {
        th.join();
    }

    <span style="color:#8b008b;font-weight:bold">if</span> (player_ != <span style="color:#8b008b;font-weight:bold">nullptr</span>)
    {
        libvlc_media_player_stop(player_);
        libvlc_media_player_release(player_);
        player_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    }
    <span style="color:#8b008b;font-weight:bold">if</span> (media_ != <span style="color:#8b008b;font-weight:bold">nullptr</span>)
    {
        libvlc_media_release(media_);
        media_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    }

    <span style="color:#8b008b;font-weight:bold">if</span> (plist_ != <span style="color:#8b008b;font-weight:bold">nullptr</span>)
    {
        libvlc_media_list_player_release(plist_);
        plist_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    }
    <span style="color:#8b008b;font-weight:bold">if</span> (list_ != <span style="color:#8b008b;font-weight:bold">nullptr</span>)
    {
        libvlc_media_list_release(list_);
        list_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    }
    <span style="color:#8b008b;font-weight:bold">if</span> (inst_ != <span style="color:#8b008b;font-weight:bold">nullptr</span>)
    {
        libvlc_release(inst_);
        inst_ = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    }

    <span style="color:#8b008b;font-weight:bold">if</span> (print_buffer != <span style="color:#8b008b;font-weight:bold">nullptr</span>)
    {
        free(print_buffer);
        print_buffer = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    }
    <span style="color:#8b008b;font-weight:bold">if</span> (out_buffer != <span style="color:#8b008b;font-weight:bold">nullptr</span>)
    {
        free(out_buffer);
        out_buffer = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    }
    <span style="color:#8b008b;font-weight:bold">if</span> (tmp_buffer != <span style="color:#8b008b;font-weight:bold">nullptr</span>)
    {
        free(tmp_buffer);
        tmp_buffer = <span style="color:#8b008b;font-weight:bold">nullptr</span>;
    }
}
